graphql.query:
query ($PROJECT: IntOrString!, $START_TIME: Date!) {
    project(projectId: $PROJECT) {
      projectData(
      start: $START_TIME,
      interval: MONTHLY,
      sdkType: [JS, ANDROID, IOS],
      groupBy: SDK_TYPE
    ) {
      resources {
        intervalStart,
        intervalEnd,
        usage {
          streamedPublishedMinutes,
          streamedSubscribedMinutes
        }
      }
    }
  }
}

query ($PROJECT: IntOrString!, $START_TIME: Date!) {
    project(projectId: $PROJECT) {
      projectData(
      start: $START_TIME,
      first: 10,
      interval: MONTHLY,

    ) {
      pageInfo {
        hasNextPage
        endCursor
      }
      resources {
        usage {
          streamedPublishedMinutes
        }
      }
    }
  }
}

query ($PROJECT: IntOrString!) {
    project(projectId: $PROJECT) {
      projectData(
      start: 0,
      first: 10,
      interval: MONTHLY,
      endCursor: "aW5zaWdodHMtcmVzb3VyY2U6MTA="

    ) {
      pageInfo {
        hasNextPage
        endCursor
      }
      resources {
        usage {
          streamedPublishedMinutes,
          streamedSubscribedMinutes
        }
      }
    }
  }
}

query ($PROJECT: IntOrString!, $SESSION_1: String!) {
    project(projectId: $PROJECT) {
      sessionData {
            sessions(sessionIds: [
                $SESSION_1
            ]) {
                resources {
                    sessionId
                    meetings {
                        totalCount
                        resources {
                            createdAt
                            connections{
                resources {
                  connectionId
                  creationTime
                  client {
                    platform      # ブラウザ情報を含むフィールド
                    os            # OS情報を含むフィールド
                    version       # ブラウザのバージョン情報を含むフィールド
                  }
                }
              }
                            destroyedAt
                            publishers {
                                resources {
                                    createdAt
                                    destroyedAt
                                    connectionId
                                    stream {
                                      streamId
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
  }
}

query ($PROJECT: IntOrString!, $SESSION: String!) {
    project(projectId: $PROJECT) {
      sessionData {
      sessions(sessionIds: [
        $SESSION,
      ]) {
        resources {
          sessionId
          meetings {
            resources {
              createdAt
              publishers {
                resources {
                  createdAt
                  connectionId
                  stream {
                    streamId
                  }
                  streamStatsCollection {
                    resources {
                      createdAt
                      audioBitrateKbps
                      videoBitrateKbps
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
  }
}

query ($PROJECT: IntOrString!, $START_TIME: Date!) {
    project(projectId: $PROJECT) {
      sessionData {
             sessionSummaries (
                 start: $START_TIME,
             ) {
                 resources {
                     sessionId
                     meetings {
                         resources {
                             maxConcurrentStreams
                             maxConcurrentStreams
                             maxConcurrentSubscribers
                             totalStreams
                             totalConnections
                         }
                    }
                }
            }
        }
  }
}
